--
-- Table structure for table `research_achievements`
--

DROP TABLE IF EXISTS `research_achievements`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `research_achievements` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '成果ID',
  `year` int NOT NULL COMMENT '年度',
  `department` varchar(100) NOT NULL COMMENT '部门名称',
  `submitter_id` int NOT NULL COMMENT '提交人ID',
  `submitter_name` varchar(50) NOT NULL COMMENT '提交人姓名',
  `achievement_name` varchar(200) NOT NULL COMMENT '成果名称',
  `field_id` int NOT NULL COMMENT '考核领域ID',
  `field_name` varchar(100) NOT NULL COMMENT '考核领域名称',
  `indicator_id` int NOT NULL COMMENT '关键指标ID',
  `indicator_name` varchar(100) NOT NULL COMMENT '关键指标名称',
  `standard_id` int NOT NULL COMMENT '评价标准ID',
  `standard_name` varchar(500) NOT NULL COMMENT '评价标准名称',
  `scoring_instruction` varchar(1000) DEFAULT NULL COMMENT '赋分说明',
  `assessment_org` varchar(100) DEFAULT NULL COMMENT '考核组织部门',
  `team_members` varchar(100) DEFAULT NULL COMMENT '团队成员',
  `obtain_date` date DEFAULT NULL COMMENT '获得日期',
  `evidence_file` varchar(255) DEFAULT NULL COMMENT '佐证材料文件路径',
  `score` int DEFAULT '0' COMMENT '得分',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态:0-草稿,1-审核中,2-已通过,3-已退回',
  `reject_reason` varchar(500) DEFAULT NULL COMMENT '退回原因',
  `reviewer_id` int DEFAULT NULL COMMENT '审核人ID',
  `reviewer_name` varchar(50) DEFAULT NULL COMMENT '审核人姓名',
  `reviewed_at` datetime DEFAULT NULL COMMENT '审核时间',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_submitter_id` (`submitter_id`),
  KEY `idx_field_id` (`field_id`),
  KEY `idx_indicator_id` (`indicator_id`),
  KEY `idx_standard_id` (`standard_id`),
  KEY `idx_status` (`status`),
  KEY `idx_year_department` (`year`,`department`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='科研成果表';
/*!40101 SET character_set_client = @saved_cs_client */;

